    private void subsidiarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_subsidiarActionPerformed
        int cant = Integer.parseInt(numero.getText());
        for(int i=0; i < cant; i++){
            lis.agregarEstudiante();
        }
    }//GEN-LAST:event_subsidiarActionPerformed

    private void informeActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_informeActionPerformed
        mostrar.setText("Estudiantes subsidiados de la facultad de: "+facultad.getText()+"\n"+lis.Informe()+"\n");
    }//GEN-LAST:event_informeActionPerformed

